mapper.select:
SELECT MEMO_ID, INTEREST, INTEREST_ID
FROM mbti_interest
WHERE MEMO_ID = #{memoId}

mapper.delete:
DELETE FROM mbti_memo
WHERE DEVICE_PK = #{devicePk}
AND MEMO_ID = #{memoId}

mapper.update:
UPDATE mbti_memo SET
    MEMO_NAME = #{memoName}
    , MEMO_AGE = #{memoAge}
    , MEMO_SEX = #{memoSex}
    , MEMO_RELATION = #{memoRelation}
    , MBTI = #{mbti}
    , MEMO_SUBMIT_DATE = now()
WHERE MEMO_ID = #{memoId}
AND DEVICE_PK = #{deviceId}
